"use strict";
const FogNode = require("./FogNode.js");
const ShaderNode = require("../ShaderNode.js");
class FogRangeNode extends FogNode {
  constructor(colorNode, nearNode, farNode) {
    super(colorNode);
    this.nearNode = nearNode;
    this.farNode = farNode;
  }
  generate(builder) {
    this.factorNode = ShaderNode.smoothstep(this.nearNode, this.farNode, ShaderNode.negate(ShaderNode.positionView.z));
    return super.generate(builder);
  }
}
FogRangeNode.prototype.isFogRangeNode = true;
module.exports = FogRangeNode;
