"use strict";
const Node = require("../core/Node.js");
const ShaderNode = require("../ShaderNode.js");
const checkerShaderNode = new ShaderNode.ShaderNode((inputs) => {
  const uv = ShaderNode.mul(inputs.uv, 2);
  const cx = ShaderNode.floor(uv.x);
  const cy = ShaderNode.floor(uv.y);
  const result = ShaderNode.mod(ShaderNode.add(cx, cy), 2);
  return ShaderNode.sign(result);
});
class CheckerNode extends Node {
  constructor(uvNode = ShaderNode.uv()) {
    super("float");
    this.uvNode = uvNode;
  }
  generate(builder) {
    return checkerShaderNode({ uv: this.uvNode }).build(builder);
  }
}
module.exports = CheckerNode;
