"use strict";
const AttributeNode = require("../core/AttributeNode.js");
class UVNode extends AttributeNode {
  constructor(index = 0) {
    super(null, "vec2");
    this.index = index;
  }
  getAttributeName() {
    return "uv" + (this.index > 0 ? this.index + 1 : "");
  }
}
module.exports = UVNode;
