"use strict";
const Node = require("./Node.js");
const constants = require("./constants.js");
class VaryNode extends Node {
  constructor(value) {
    super();
    this.value = value;
  }
  getType(builder) {
    return this.value.getType(builder);
  }
  generate(builder, output) {
    const type = this.getType(builder);
    const snippet = this.value.buildStage(builder, constants.NodeShaderStage.Vertex, type);
    const nodeVary = builder.getVaryFromNode(this, type);
    nodeVary.snippet = snippet;
    const propertyName = builder.getPropertyName(nodeVary);
    return builder.format(propertyName, type, output);
  }
}
module.exports = VaryNode;
