"use strict";
const FloatNode = require("../inputs/FloatNode.js");
const constants = require("../core/constants.js");
class TimerNode extends FloatNode {
  constructor() {
    super();
    this.updateType = constants.NodeUpdateType.Frame;
  }
  update(frame) {
    this.value = frame.time;
  }
}
module.exports = TimerNode;
