"use strict";
const WebGPUBinding = require("./WebGPUBinding.js");
const constants = require("./constants.js");
class WebGPUSampler extends WebGPUBinding {
  constructor(name, texture) {
    super(name);
    this.texture = texture;
    this.type = constants.GPUBindingType.Sampler;
    this.visibility = GPUShaderStage.FRAGMENT;
    this.samplerGPU = null;
    Object.defineProperty(this, "isSampler", { value: true });
  }
}
module.exports = WebGPUSampler;
